import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Typography,
    Card,
    CardContent,
    Grid,
    Tabs,
    Tab,
    CircularProgress,
    TextField,
    InputAdornment,
    Chip,
    Button,
    LinearProgress,
    IconButton,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import { useNavigate } from 'react-router-dom';
import practiceService from '../services/practiceService';

const PracticePage = () => {
    const navigate = useNavigate();
    const [practiceSets, setPracticeSets] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedType, setSelectedType] = useState('all');

    // Configuration: set to true to hide specific question types
    const config = {
        hideTypes: {
            'mcq': true,  // Set to true to hide MCQ questions
            'fill_blanks': false,
            'factual': false,
            'novice': false
        }
    };

    // Helper function to get user-friendly type name
    const getTypeDisplayName = (type) => {
        switch(type) {
            case 'mcq':
                return 'Multiple Choice';
            case 'fill_blanks':
                return 'Fill in Blanks';
            case 'factual':
                return 'Factual';
            case 'novice':
                return 'Novice';
            default:
                return type.charAt(0).toUpperCase() + type.slice(1);
        }
    };

    // Fetch practice sets from MongoDB
    useEffect(() => {
        const fetchPracticeSets = async () => {
            try {
                setLoading(true);
                const data = await practiceService.getPracticeSets();
                console.log("Raw practice sets from MongoDB:", data);

                // Filter out unwanted question types
                const filteredData = data.filter(set => !config.hideTypes[set.type]);

                // Modify the titles for display purposes
                const modifiedData = (filteredData || []).map((set, index) => {
                    if (!set.title || set.title === "Untitled") {
                        return { ...set, title: `Practice ${index + 1}` };
                    }
                    return set;
                });
                setPracticeSets(modifiedData);
                setError(null);
            } catch (error) {
                console.error('Error fetching practice sets:', error);
                setError(error.message || 'Failed to fetch practice sets');
            } finally {
                setLoading(false);
            }
        };

        fetchPracticeSets();
    }, []);

    // Get available types from the practice sets
    const availableTypes = React.useMemo(() => {
        const types = new Set();
        practiceSets.forEach(set => {
            if (set.type) {
                types.add(set.type);
            }
        });
        return Array.from(types);
    }, [practiceSets]);

    // Filter practice sets based on type and search query
    const filteredSets = (practiceSets || []).filter(set => {
        const matchesSearch = set.title?.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesType = selectedType === 'all' || set.type === selectedType;
        return matchesSearch && matchesType;
    });

    const handleTypeChange = (event, newValue) => {
        setSelectedType(newValue);
    };

    if (loading) {
        return (
            <Container maxWidth="lg" sx={{ mt: 4 }}>
                <LinearProgress />
            </Container>
        );
    }

    if (error) {
        return (
            <Container maxWidth="lg" sx={{ mt: 4 }}>
                <Typography color="error" variant="h6">
                    {error}
                </Typography>
            </Container>
        );
    }

    return (
        <Container maxWidth="lg" sx={{ mt: 4 }}>
            <Typography variant="h4" gutterBottom>
                Practice Hindi
            </Typography>

            {/* Filters and Search */}
            <Box sx={{ mb: 4 }}>
                <Grid container spacing={2} alignItems="center">
                    <Grid item xs={12} md={6}>
                        <TextField
                            fullWidth
                            placeholder="Search practice sets..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <SearchIcon />
                                    </InputAdornment>
                                ),
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <Tabs
                            value={selectedType}
                            onChange={handleTypeChange}
                            variant="scrollable"
                            scrollButtons="auto"
                        >
                            <Tab label="All Types" value="all" />
                            {availableTypes.map(type => (
                                <Tab 
                                    key={type} 
                                    label={getTypeDisplayName(type)} 
                                    value={type} 
                                />
                            ))}
                        </Tabs>
                    </Grid>
                </Grid>
            </Box>

            {/* Practice Sets Grid */}
            <Grid container spacing={3}>
                {filteredSets.length > 0 ? (
                    filteredSets.map((set) => (
                        <Grid item xs={12} sm={6} md={4} key={`${set.video_id}-${set.type}`}>
                            <Card 
                                sx={{ 
                                    height: '100%',
                                    display: 'flex',
                                    flexDirection: 'column',
                                    cursor: 'pointer',
                                    '&:hover': {
                                        boxShadow: 6,
                                    },
                                }}
                                onClick={() => navigate(`/practice/${set.video_id}/${set.type}`)}
                            >
                                <CardContent>
                                    <Typography variant="h6" gutterBottom>
                                        {set.title}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        Type: {getTypeDisplayName(set.type)}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        Questions: {set.questionCount}
                                    </Typography>
                                    <LinearProgress 
                                        variant="determinate" 
                                        value={set.progress || 0}
                                        sx={{ mt: 2 }}
                                    />
                                    <Typography variant="body2" color="text.secondary" align="right">
                                        {Math.round(set.progress || 0)}% Complete
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))
                ) : (
                    <Grid item xs={12}>
                        <Typography variant="h6" align="center" color="text.secondary">
                            No practice sets found
                        </Typography>
                    </Grid>
                )}
            </Grid>
        </Container>
    );
};

export default PracticePage;
