import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    Box,
    Card,
    CardContent,
    Button,
    LinearProgress,
    TextField,
    Alert,
    Grid,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    Collapse,
    IconButton,
    Paper,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import practiceService from '../services/practiceService';

// Helper function to get user-friendly type name
const getTypeDisplayName = (type) => {
    switch(type) {
        case 'mcq':
            return 'Multiple Choice';
        case 'fill_blanks':
            return 'Fill in Blanks';
        case 'factual':
            return 'Factual';
        case 'novice':
            return 'Novice';
        default:
            return type.charAt(0).toUpperCase() + type.slice(1);
    }
};

const PracticeSetPage = () => {
    const { videoId, type } = useParams();
    const navigate = useNavigate();
    const [questions, setQuestions] = useState([]);
    const [progress, setProgress] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answer, setAnswer] = useState('');
    const [feedback, setFeedback] = useState(null);
    const [showTranscript, setShowTranscript] = useState(false);
    const [transcript, setTranscript] = useState('');

    // Configuration: set to true to hide specific question types (must match PracticePage.js)
    const config = {
        hideTypes: {
            'mcq': true,  // Set to true to hide MCQ questions
            'fill_blanks': false,
            'factual': false,
            'novice': false
        }
    };

    useEffect(() => {
        // If this is a hidden question type, show error and don't fetch
        if (config.hideTypes[type]) {
            setLoading(false);
            setError(`${getTypeDisplayName(type)} questions are not available in this application.`);
            return;
        }

        const fetchQuestions = async () => {
            try {
                setLoading(true);
                const data = await practiceService.getPracticeQuestions(videoId, type);

                if (!data.questions || data.questions.length === 0) {
                    setError(`No ${getTypeDisplayName(type)} questions available for this practice set.`);
                    setQuestions([]);
                } else {
                    setQuestions(data.questions || []);
                    setProgress(data.progress || {});
                    setTranscript(data.transcript || '');
                    setError(null);
                }
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError(err.message || 'Failed to fetch questions');
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, [videoId, type]);

    const moveToNextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(prev => prev + 1);
            setAnswer('');
            setFeedback(null);
        }
    };

    const handleSubmitAnswer = async () => {
        if (!answer.trim()) return;

        const currentQuestion = questions[currentQuestionIndex];
        try {
            // Clean up both answers for comparison
            const cleanUserAnswer = answer.toLowerCase().trim().replace(/\s+/g, ' ');
            const cleanCorrectAnswer = currentQuestion.answer.toLowerCase().trim().replace(/\s+/g, ' ');
            const isCorrect = cleanUserAnswer === cleanCorrectAnswer;

            await practiceService.submitAnswer({
                question_id: currentQuestion._id,
                answer: answer,
                video_id: videoId,
                type: type,
                is_correct: isCorrect
            });

            if (isCorrect) {
                setFeedback({
                    type: 'success',
                    message: 'Correct!'
                });
                setProgress(prev => ({
                    ...prev,
                    [currentQuestion._id]: {
                        answer,
                        is_correct: true,
                        submitted_at: new Date().toISOString()
                    }
                }));

                // Move to next question after a short delay
                setTimeout(moveToNextQuestion, 1000);
            } else {
                setFeedback({
                    type: 'error',
                    message: `Incorrect. The correct answer is: ${currentQuestion.answer}`
                });
            }
        } catch (err) {
            console.error('Error submitting answer:', err);
            setFeedback({
                type: 'error',
                message: 'Failed to submit answer. Please try again.'
            });
        }
    };

    if (loading) {
        return (
            <Container maxWidth="md" sx={{ mt: 4 }}>
                <LinearProgress />
            </Container>
        );
    }

    if (error) {
        return (
            <Container maxWidth="md" sx={{ mt: 4 }}>
                <Alert severity="error">{error}</Alert>
            </Container>
        );
    }

    const currentQuestion = questions[currentQuestionIndex];
    const progressPercentage = (Object.keys(progress).length / questions.length) * 100;

    return (
        <Container maxWidth="md" sx={{ mt: 4 }}>
            <Box sx={{ mb: 4 }}>
                <Typography variant="h4" gutterBottom>
                    Practice Set - {getTypeDisplayName(type)}
                </Typography>
                <LinearProgress 
                    variant="determinate" 
                    value={progressPercentage} 
                    sx={{ mt: 2, mb: 1 }}
                />
                <Typography variant="body2" color="text.secondary">
                    Progress: {Math.round(progressPercentage)}% ({Object.keys(progress).length}/{questions.length} questions)
                </Typography>
            </Box>

            {transcript && (
                <Card sx={{ mb: 4 }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
                            <Typography variant="h6">Transcript</Typography>
                            <IconButton onClick={() => setShowTranscript(!showTranscript)}>
                                {showTranscript ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                            </IconButton>
                        </Box>
                        <Collapse in={showTranscript}>
                            <Paper 
                                sx={{ 
                                    p: 2, 
                                    backgroundColor: 'background.paper',
                                    maxHeight: '400px',
                                    overflow: 'auto'
                                }}
                            >
                                <Typography 
                                    variant="body1" 
                                    sx={{ 
                                        whiteSpace: 'pre-wrap',
                                        lineHeight: 1.8
                                    }}
                                >
                                    {transcript}
                                </Typography>
                            </Paper>
                        </Collapse>
                    </CardContent>
                </Card>
            )}

            {currentQuestion && (
                <Card sx={{ mb: 4 }}>
                    <CardContent>
                        <Typography variant="h6" gutterBottom>
                            Question {currentQuestionIndex + 1} of {questions.length}
                        </Typography>
                        <Typography variant="body1" sx={{ mb: 3 }}>
                            {currentQuestion.question_text}
                        </Typography>

                        <TextField
                            fullWidth
                            label="Your Answer"
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)}
                            variant="outlined"
                            sx={{ mb: 2 }}
                            onKeyPress={(e) => {
                                if (e.key === 'Enter' && answer.trim()) {
                                    handleSubmitAnswer();
                                }
                            }}
                            autoFocus
                        />

                        {feedback && (
                            <Alert severity={feedback.type} sx={{ mt: 2, mb: 2 }}>
                                {feedback.message}
                            </Alert>
                        )}

                        <Grid container spacing={2} sx={{ mt: 2 }}>
                            <Grid item>
                                <Button
                                    variant="contained"
                                    onClick={handleSubmitAnswer}
                                    disabled={!answer.trim()}
                                >
                                    Submit Answer
                                </Button>
                            </Grid>
                            {feedback && feedback.type === 'error' && (
                                <Grid item>
                                    <Button
                                        variant="outlined"
                                        onClick={moveToNextQuestion}
                                    >
                                        Next Question
                                    </Button>
                                </Grid>
                            )}
                        </Grid>
                    </CardContent>
                </Card>
            )}
        </Container>
    );
};

export default PracticeSetPage;
